import React, { useEffect, useState } from 'react';
import { Box, Container, Typography, Grid, Paper } from '@mui/material';
import WalletTable from './components/WalletTable';
import ConfigForm from './components/ConfigForm';
import TransactionLog from './components/TransactionLog';
import axios from 'axios';

interface MainWallet {
  publicKey: string;
  balance: number;
}

interface Wallet {
  publicKey: string;
  solBalance: number;
  tokenBalances: { [mint: string]: number };
  tradeStatus: string;
}

interface TransactionLog {
  wallet: string;
  type: string;
  signature: string;
  status: string;
  details: string;
  timestamp: string;
}

interface Config {
  swapAmountLamports: number;
  tokenList: { symbol: string; mint: string }[];
  tradeMode: 'random' | 'buyOnly' | 'sellOnly';
  concurrencyLimit: number;
}

const App: React.FC = () => {
  const [mainWallet, setMainWallet] = useState<MainWallet | null>(null);
  const [wallets, setWallets] = useState<Wallet[]>([]);
  const [transactionLogs, setTransactionLogs] = useState<TransactionLog[]>([]);
  const [config, setConfig] = useState<Config | null>(null);

  useEffect(() => {
  let ws: WebSocket;
  const connectWebSocket = () => {
    ws = new WebSocket('ws://131.153.227.56:3002');
    ws.onopen = () => console.log('WebSocket connected');
    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        setMainWallet(data.mainWallet);
        setWallets(data.wallets);
        setTransactionLogs(data.transactionLogs);
        setConfig(data.config);
      } catch (err) {
        console.error('WebSocket message error:', err);
      }
    };
    ws.onclose = () => {
      console.log('WebSocket disconnected, reconnecting...');
      setTimeout(connectWebSocket, 5000);
    };
    ws.onerror = (err) => console.error('WebSocket error:', err);
  };
  connectWebSocket();
  return () => {
    if (ws) ws.close();
  };
}, []);

  const handleCreateWallets = async () => {
  try {
    const response = await axios.post('http://131.153.227.56:3001/api/create-wallets', {}, {
      timeout: 10000,
      headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' }
    });
    console.log('Create wallets response:', response.data);
    alert('Wallets created successfully');
  } catch (err) {
    console.error('Create wallets error:', err);
    if (axios.isAxiosError(err)) {
      console.error('Axios details:', {
        message: err.message,
        code: err.code,
        status: err.response?.status,
        data: err.response?.data,
        url: err.config?.url
      });
    }
    alert('Error creating wallets: ' + (err as Error).message);
  }
};

  const handleFundWallets = async () => {
  try {
    const response = await axios.post('http://131.153.227.56:3001/api/fund-wallets', {}, {
      timeout: 10000,
      headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' }
    });
    console.log('Fund wallets response:', response.data);
    alert('Wallets funded successfully');
  } catch (err) {
    console.error('Fund wallets error:', err);
    if (axios.isAxiosError(err)) {
      console.error('Axios details:', {
        message: err.message,
        code: err.code,
        status: err.response?.status,
        data: err.response?.data,
        url: err.config?.url
      });
    }
    alert('Error funding wallets: ' + (err as Error).message);
  }
};

const handleTradeWallets = async () => {
  try {
    const response = await axios.post('http://131.153.227.56:3001/api/trade-wallets', {}, {
      timeout: 10000,
      headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' }
    });
    console.log('Trade wallets response:', response.data);
    alert('Trading started successfully');
  } catch (err) {
    console.error('Trade wallets error:', err);
    if (axios.isAxiosError(err)) {
      console.error('Axios details:', {
        message: err.message,
        code: err.code,
        status: err.response?.status,
        data: err.response?.data,
        url: err.config?.url
      });
    }
    alert('Error starting trading: ' + (err as Error).message);
  }
};

const handleConfigUpdate = async (newConfig: Partial<Config>) => {
  try {
    const response = await axios.post('http://131.153.227.56:3001/api/config', newConfig, {
      timeout: 10000,
      headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' }
    });
    console.log('Config update response:', response.data);
    alert('Config updated successfully');
  } catch (err) {
    console.error('Config update error:', err);
    if (axios.isAxiosError(err)) {
      console.error('Axios details:', {
        message: err.message,
        code: err.code,
        status: err.response?.status,
        data: err.response?.data,
        url: err.config?.url
      });
    }
    alert('Error updating config: ' + (err as Error).message);
  }
};

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" gutterBottom>
          Solana Trading Bot
        </Typography>
        {mainWallet && (
          <Paper sx={{ p: 2, mb: 4 }}>
            <Typography variant="h6">
              Main Wallet: {mainWallet.publicKey}
            </Typography>
            <Typography>
              Balance: {(mainWallet.balance / 1_000_000_000).toFixed(4)} SOL
            </Typography>
          </Paper>
        )}
        <Grid container spacing={4}>
          <Grid item xs={12} md={6}>
            <WalletTable
              wallets={wallets}
              onCreateWallets={handleCreateWallets}
              onFundWallets={handleFundWallets}
              onTradeWallets={handleTradeWallets}
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <ConfigForm config={config} onConfigUpdate={handleConfigUpdate} />
          </Grid>
          <Grid item xs={12}>
            <TransactionLog logs={transactionLogs} />
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
};

export default App;
