import { useState, useEffect } from 'react';
import { Container, Box, Typography, Paper, Grid, TextField, Button } from '@mui/material';
import axios from 'axios';
import WalletTable from './components/WalletTable';
import ConfigForm from './components/ConfigForm';
import TransactionLog from './components/TransactionLog';

interface MainWallet {
  publicKey: string;
  balance: number;
}

interface Wallet {
  publicKey: string;
  solBalance: number;
  tokenBalances: { [key: string]: number };
  tradeStatus: string;
}

interface TransactionLog {
  wallet: string;
  type: string;
  signature: string;
  status: string;
  details: string;
  timestamp: string;
}

interface Config {
  // Define your config interface based on your needs
  swapAmount?: number;
  tradeMode?: string;
  concurrencyLimit?: number;
  tokenAddress?: string;
}

const App: React.FC = () => {
  const [mainWallet, setMainWallet] = useState<MainWallet | null>(null);
  const [wallets, setWallets] = useState<Wallet[]>([]);
  const [transactionLogs, setTransactionLogs] = useState<TransactionLog[]>([]);
  const [config, setConfig] = useState<Config | null>(null);
  const [fundAmount, setFundAmount] = useState('0.001');

  useEffect(() => {
    // Fetch initial data (main wallet, wallets, config, logs)
    const fetchData = async () => {
      try {
        const [mainWalletRes, walletsRes, configRes, logsRes] = await Promise.all([
          axios.get('http://131.153.227.56:3001/api/main-wallet'),
          axios.get('http://131.153.227.56:3001/api/wallets'),
          axios.get('http://131.153.227.56:3001/api/config'),
          axios.get('http://131.153.227.56:3001/api/transaction-logs'),
        ]);
        setMainWallet(mainWalletRes.data);
        setWallets(walletsRes.data);
        setConfig(configRes.data);
        setTransactionLogs(logsRes.data);
      } catch (err) {
        console.error('Error fetching initial data:', err);
      }
    };
    fetchData();

    // WebSocket for real-time updates
    const ws = new WebSocket('ws://131.153.227.56:3002');
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'mainWallet') setMainWallet(data.payload);
      if (data.type === 'wallets') setWallets(data.payload);
      if (data.type === 'transactionLogs') setTransactionLogs(data.payload);
      if (data.type === 'config') setConfig(data.payload);
    };
    return () => ws.close();
  }, []);

  const handleCreateWallets = async () => {
    try {
      await axios.post('http://131.153.227.56:3001/api/create-wallets');
      const walletsRes = await axios.get('http://131.153.227.56:3001/api/wallets');
      setWallets(walletsRes.data);
      alert('Wallets created successfully');
    } catch (err) {
      console.error('Error creating wallets:', err);
      alert('Error creating wallets: ' + (err as Error).message);
    }
  };

  const handleFundWallets = async () => {
    try {
      const amount = parseFloat(fundAmount);
      if (isNaN(amount) || amount <= 0) {
        alert('Please enter a valid positive amount');
        return;
      }
      const response = await axios.post('http://131.153.227.56:3001/api/fund-wallets', {
        fundAmount: amount * 1_000_000_000, // SOL to lamports
      }, {
        timeout: 10000,
        headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' }
      });
      console.log('Fund wallets response:', response.data);
      alert('Wallets funded successfully');
    } catch (err) {
      console.error('Fund wallets error:', err);
      if (axios.isAxiosError(err)) {
        console.error('Axios details:', {
          message: err.message,
          code: err.code,
          status: err.response?.status,
          data: err.response?.data,
          url: err.config?.url
        });
      }
      alert('Error funding wallets: ' + (err as Error).message);
    }
  };

  const handleTradeWallets = async () => {
    try {
      await axios.post('http://131.153.227.56:3001/api/trade-wallets');
      alert('Trading started successfully');
    } catch (err) {
      console.error('Error trading wallets:', err);
      alert('Error trading wallets: ' + (err as Error).message);
    }
  };

  const handleConfigUpdate = async (newConfig: Config) => {
    try {
      await axios.post('http://131.153.227.56:3001/api/config', newConfig);
      setConfig(newConfig);
      alert('Config updated successfully');
    } catch (err) {
      console.error('Error updating config:', err);
      alert('Error updating config: ' + (err as Error).message);
    }
  };

  const handleRestoreSol = async () => {
    try {
      const response = await axios.post('http://131.153.227.56:3001/api/restore-sol', {}, {
        timeout: 10000,
        headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' }
      });
      console.log('Restore SOL response:', response.data);
      alert('SOL restored successfully');
    } catch (err) {
      console.error('Restore SOL error:', err);
      if (axios.isAxiosError(err)) {
        console.error('Axios details:', {
          message: err.message,
          code: err.code,
          status: err.response?.status,
          data: err.response?.data,
          url: err.config?.url
        });
      }
      alert('Error restoring SOL: ' + (err as Error).message);
    }
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" gutterBottom>
          Solana Trading Bot
        </Typography>
        {mainWallet && (
          <Paper sx={{ p: 2, mb: 4 }}>
            <Typography variant="h6">
              Main Wallet: {mainWallet.publicKey}
            </Typography>
            <Typography>
              Balance: {(mainWallet.balance / 1_000_000_000).toFixed(4)} SOL
            </Typography>
          </Paper>
        )}
        <Grid container spacing={4}>
          <Grid item xs={12} md={6}>
            <Box sx={{ mb: 2 }}>
              <TextField
                label="Fund Amount (SOL)"
                value={fundAmount}
                onChange={(e) => setFundAmount(e.target.value)}
                type="number"
                inputProps={{ step: '0.001', min: '0' }}
                sx={{ mr: 1 }}
              />
              <Button
                variant="contained"
                onClick={handleFundWallets}
                sx={{ mr: 1 }}
              >
                Fund Wallets
              </Button>
              <Button
                variant="contained"
                onClick={handleRestoreSol}
              >
                Restore SOL to Main Wallet
              </Button>
            </Box>
            <WalletTable
              wallets={wallets}
              onCreateWallets={handleCreateWallets}
              onFundWallets={handleFundWallets}
              onTradeWallets={handleTradeWallets}
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <ConfigForm config={config} onConfigUpdate={handleConfigUpdate} />
          </Grid>
          <Grid item xs={12}>
            <TransactionLog logs={transactionLogs} />
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
};

export default App;
