import { useState } from 'react';
import {
  Box,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Typography,
} from '@mui/material';
import axios from 'axios';
import { ConfigType } from '../types';

interface ConfigFormProps {
  config: ConfigType | null;
  onConfigUpdate: (newConfig: Partial<ConfigType>) => void;
}

const ConfigForm: React.FC<ConfigFormProps> = ({ config, onConfigUpdate }) => {
  const [swapAmount, setSwapAmount] = useState(config?.swapAmount?.toString() || '0.1');
  const [tradeMode, setTradeMode] = useState(config?.tradeMode || 'random');
  const [concurrencyLimit, setConcurrencyLimit] = useState(config?.concurrencyLimit?.toString() || '2');
  const [tokenAddress, setTokenAddress] = useState(config?.tokenAddress || '');
  const [tokenListJson, setTokenListJson] = useState(JSON.stringify(config?.tokenList || [], null, 4));
  const [jsonError, setJsonError] = useState('');
  const [tradingStatus, setTradingStatus] = useState({
    buy: false,
    sell: false,
    random: false,
  });

  const handleUpdateConfig = async () => {
    try {
      const parsedTokenList = JSON.parse(tokenListJson);
      if (!Array.isArray(parsedTokenList)) throw new Error('Token list must be an array');
      for (const token of parsedTokenList) {
        if (!token.symbol || !token.mint) throw new Error('Each token must have symbol and mint');
      }
      const newConfig: Partial<ConfigType> = {
        swapAmount: parseFloat(swapAmount),
        tradeMode,
        concurrencyLimit: parseInt(concurrencyLimit),
        tokenAddress,
        tokenList: parsedTokenList,
      };
      await axios.post('http://131.153.227.56:3001/api/config', newConfig, { timeout: 30000 });
      onConfigUpdate(newConfig);
      setJsonError('');
      alert('Config updated successfully');
    } catch (err) {
      setJsonError(`Invalid JSON or request failed: ${(err as Error).message}`);
    }
  };

  const handleTradeAction = async (action: 'start' | 'stop', mode: 'buy' | 'sell' | 'random') => {
    try {
      const endpoint = `/api/${action}-${mode}-trade`;
      await axios.post(`http://131.153.227.56:3001${endpoint}`, {}, { timeout: 30000 });
      setTradingStatus((prev) => ({
        ...prev,
        [mode]: action === 'start',
      }));
      alert(`${mode.charAt(0).toUpperCase() + mode.slice(1)} trading ${action}ed`);
    } catch (err) {
      console.error(`Error ${action}ing ${mode} trade:`, err);
      alert(`Failed to ${action} ${mode} trading: ${(err as Error).message}`);
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
      <TextField
        label="Swap Amount (SOL)"
        value={swapAmount}
        onChange={(e) => setSwapAmount(e.target.value)}
        type="number"
        inputProps={{ step: '0.01' }}
      />
      <FormControl>
        <InputLabel>Trade Mode</InputLabel>
        <Select value={tradeMode} onChange={(e) => setTradeMode(e.target.value)}>
          <MenuItem value="random">Random</MenuItem>
          <MenuItem value="sequential">Sequential</MenuItem>
        </Select>
      </FormControl>
      <TextField
        label="Concurrency Limit"
        value={concurrencyLimit}
        onChange={(e) => setConcurrencyLimit(e.target.value)}
        type="number"
        inputProps={{ min: '1' }}
      />
      <TextField
        label="Token Address"
        value={tokenAddress}
        onChange={(e) => setTokenAddress(e.target.value)}
      />
      <TextField
        label="Token List (JSON)"
        value={tokenListJson}
        onChange={(e) => setTokenListJson(e.target.value)}
        multiline
        rows={4}
        error={!!jsonError}
        helperText={jsonError}
      />
      {config?.tokenList && config.tokenList.length > 0 && (
        <Box>
          <Typography variant="subtitle1" gutterBottom>
            Current Token List
          </Typography>
          <Table size="small">
            <TableHead>
              <TableRow>
                <TableCell>Symbol</TableCell>
                <TableCell>Mint</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {config.tokenList.map((token, index) => (
                <TableRow key={index}>
                  <TableCell>{token.symbol}</TableCell>
                  <TableCell>{token.mint}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Box>
      )}
      <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
        <Button variant="contained" color="primary" onClick={handleUpdateConfig}>
          Update Config
        </Button>
        <Button
          variant="contained"
          color={tradingStatus.buy ? 'secondary' : 'primary'}
          onClick={() => handleTradeAction(tradingStatus.buy ? 'stop' : 'start', 'buy')}
        >
          {tradingStatus.buy ? 'Stop Buy' : 'Start Buy'}
        </Button>
        <Button
          variant="contained"
          color={tradingStatus.sell ? 'secondary' : 'primary'}
          onClick={() => handleTradeAction(tradingStatus.sell ? 'stop' : 'start', 'sell')}
        >
          {tradingStatus.sell ? 'Stop Sell' : 'Start Sell'}
        </Button>
        <Button
          variant="contained"
          color={tradingStatus.random ? 'secondary' : 'primary'}
          onClick={() => handleTradeAction(tradingStatus.random ? 'stop' : 'start', 'random')}
        >
          {tradingStatus.random ? 'Stop Random' : 'Start Random'}
        </Button>
      </Box>
    </Box>
  );
};

export default ConfigForm;
