import React from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Typography,
} from '@mui/material';

interface Wallet {
  publicKey: string;
  solBalance: number;
  tokenBalances: { [mint: string]: number };
  tradeStatus: string;
}

interface WalletTableProps {
  wallets: Wallet[];
  onCreateWallets: () => void;
  onFundWallets: () => void;
  onTradeWallets: () => void;
}

const WalletTable: React.FC<WalletTableProps> = ({
  wallets,
  onCreateWallets,
  onFundWallets,
  onTradeWallets,
}) => {
  return (
    <Paper sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        Sub-Wallets
      </Typography>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Public Key</TableCell>
              <TableCell>SOL Balance</TableCell>
              <TableCell>Token Balances</TableCell>
              <TableCell>Trade Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {wallets.map((wallet) => (
              <TableRow key={wallet.publicKey}>
                <TableCell>{wallet.publicKey.slice(0, 8)}...</TableCell>
                <TableCell>
                  {(wallet.solBalance / 1_000_000_000).toFixed(4)} SOL
                </TableCell>
                <TableCell>
                  {Object.entries(wallet.tokenBalances).map(([mint, balance]) => (
                    <div key={mint}>
                      {mint.slice(0, 8)}...: {balance}
                    </div>
                  ))}
                </TableCell>
                <TableCell>{wallet.tradeStatus}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Button
        variant="contained"
        sx={{ mt: 2, mr: 1 }}
        onClick={onCreateWallets}
      >
        Create Wallets
      </Button>
      <Button
        variant="contained"
        sx={{ mt: 2, mr: 1 }}
        onClick={onFundWallets}
      >
        Fund Wallets
      </Button>
      <Button variant="contained" sx={{ mt: 2 }} onClick={onTradeWallets}>
        Trade Wallets
      </Button>
    </Paper>
  );
};

export default WalletTable;
